INSERT INTO `job` VALUES ('1278', 'https://www.lagou.com/jobs/5488914.html', '2019-03-21 00:00:00', '杭州', '西湖区', '西溪', '无端科技技术部招聘', '资深Java服务端开发工程师', '30', '40', '30k-40k /杭州 / 经验5-10年 / 本科及以上 / 全职\n后端\n服务器端\n2019-03-21  发布于拉勾网', '后端,服务器端', '扁平管理、五险一金、周末双休、下午茶', '岗位职责：\n1、负责游戏功能系统的服务端开发工作;\n2、游戏服务端进行需求分析和架构设计，并完成相应的文档;\n3、服务端相关工具链开发;\n\n任职资格：\n1、5年以上工作经验（java方向），有高并发、高可用、高性能系统设计经验;\n2、精通Java语言，熟悉IO、多线程、集合等基础框架以及使用场景、对JVM原理有较深入的了解;\n3、熟悉Netty等常见的网络框架，理解内部原理;\n4、优秀的系统设计和架构能力;\n5、良好的沟通和协调能力，工作主动性强;\n6、诚恳、踏实、抗压，对技术和工作充满热情。', '杭州 - 西湖区 - 北山 - 浙江省杭州市西湖区新湖商务大厦4-5楼', '2019-03-27 23:19:48', '2019-03-27 23:19:48');
